    let prod_id = localStorage.getItem('productEditId');
    const TOKEN = 'token'
    const { productCtx, setProductCtx } = React.useContext(ProductContext);
    const [variant, setVariant] = useState({
        metal_color: [],
        metal_purity: [],
        diamond_color: [],
        diamond_clarity: [],
        product_images: {},
        size: []

    })
