import React from 'react';
import { Grid, FormGroup, FormControlLabel, Checkbox } from '@material-ui/core';
import { ProductContext } from '../../context';
export function CreateVariant(props) {

    console.log(productCtx,'createvariants')
    function handleChange(status) {
alert(JSON.stringify(productCtx))
    }
    return (
        <Grid container>
            <Grid item>
                <Grid container>
                    <Grid>Metal Color</Grid>
                    <FormGroup row>
                        {
                            productCtx.masterData.metalcolour.map((data,index)=>(

                                  productCtx.productMetalColor.some((prod_metal_color)=>prod_metal_color.productColor==data.name)?     
                                     <FormControlLabel
                                    disabled
                                    control={
                                        <Checkbox checked={true} onChange={handleChange('checkedA')} value="checkedA" />
                                    }
                                    label={data.name}
                                />:     
                                <FormControlLabel
                                control={
                                    <Checkbox checked={true} onChange={handleChange('checkedA')} value="checkedA" />
                                }
                                label={data.name}
                            />

                            ))
                        }
                    </FormGroup>
                </Grid>
            </Grid>
        </Grid>
    )
}
export const ProductAttributes = withRouter(props => {

    const classes = useStyles2();

    const { productCtx:{prodductname}, setProductCtx } = React.useContext(ProductContext); 
    const { data, loading, error } = useQuery(productCategory.query);

    if (loading) return <div className="loaderdiv"><CircularProgress className={classes.progress} />
    </div>
    if (error) return <div>error</div>

    const _content = <ProductProvider value={{ data, mapper: productCategory.mapper, mappertype: "masterData" }} >
      <Component {...props} productCtx={productCtx.prodductname}/>
    </ProductProvider>
    return _content
});
export default CreateVariant;
