import React,{useEffect,useContext} from 'react';
import { makeStyles } from '@material-ui/styles';
import { Grid, TextField } from '@material-ui/core';
import {Input} from '../../components/Input.js'
import { ProductContext,ProductProvider } from '../../context';
import { withRouter } from "react-router-dom";
import DiamondDetails from './DiamondDetails';
import GemstoneDetails from './GemstoneDetails';
import Variants from './Variants';
import { productCategory } from '../../services/mapper';
import { useQuery } from 'react-apollo';
import CircularProgress from '@material-ui/core/CircularProgress';
import  "../Productupload/Productupload.css";
import { PRODUCTEDIT } from '../../graphql/query';
const useStyle = makeStyles(theme=>({
    helperinput: {
        '& .MuiFormHelperText-root':{
          color: "#e53935",
        }
      }
}))

export  function Component (props){

    const { productCtx, setProductCtx } = useContext(ProductContext);
    console.log(productCtx.productname)
    const classes  = useStyle();
    const keyPress =type => e => {
        console.log('product',productCtx,e)
        const re = /^[a-zA-Z\b]+$/;
        if (e.target.value === '' || re.test(e.target.value)) {
          setProductCtx({ ...productCtx, [type]: e.target.value})
        }

     }
    useEffect(()=>{
      const url = "https://api-alpha.stylori.net/graphql";
      const opts = {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query:PRODUCTEDIT,variables:{"productId":"WyJwcm9kdWN0X2xpc3RzIiwiU0IwMDEwIl0="} })
      };
      // console.log("helo",setProductCtx)
        fetch(url, opts)
        .then(res => res.json())
        .then(fatchvalue=>{
          setProductCtx({
            ...productCtx,
            productname: fatchvalue.data.productList.productName,
            product_type:fatchvalue.data.productList.productType,
            product_categoy: fatchvalue.data.productList.productCategory,
            gemstonelist: fatchvalue.data.productList.productGemstonesByProductSku.nodes,
            diamondlist:fatchvalue. data.productList.productDiamondsByProductSku.nodes,
            variants:fatchvalue.data.productList.transSkuListsByProductId.nodes,
            product_images:fatchvalue.data.productList.productImagesByProductId.nodes
          })
        })
        .catch(console.error)
     },[])
    return(
        <Grid container spacing={1} >
            <Grid item xs={12} sm={12} md={9} spacing={2} style={{ padding:"15px"}}>
              <Grid container item md={6}>
                    <TextField  
                          className={classes.helperinput}
                          variant="outlined"
                          margin="dense"
                          fullWidth
                          defaultValue={productCtx.productname}
                          id="productname"
                          error = {productCtx && productCtx.error_message && productCtx.error_message.productname}
                          name="productname"
                          label="Product Name"
                          onChange={keyPress('productname')}
                      />
              </Grid>  
              <DiamondDetails /> 
              <GemstoneDetails/>
              <Variants />
            </Grid>
            <Grid item  xs={12} sm={12} md={3} spacing={2} style={{ padding:"15px", backgroundColor: "#FFFFFF"}}>
            <TextField
                          className={classes.helperinput}
                          variant="outlined"
                          margin="dense"
                          fullWidth
                          defaultValue={productCtx.productname}
                          InputProps={{
                            readOnly: true,
                          }}
                          id="short_description"
                          error = {productCtx && productCtx.error_message && productCtx.error_message.productname}
                          InputProps={{
                            readOnly: true,
                          }}
                          name="short_description"
                          label="Short Description"

                        />
                       <TextField
                          className={classes.helperinput}
                          variant="outlined"
                          margin="dense"
                          fullWidth
                          defaultValue={productCtx.productname}
                          id="seo_text"
                          error = {productCtx && productCtx.error_message && productCtx.error_message.productname}
                          InputProps={{
                            readOnly: true,
                          }}
                          name="seo_text"
                          label="SEO Text"

                        />
                        <TextField
                          className={classes.helperinput}
                          variant="outlined"
                          margin="dense"
                          fullWidth
                          defaultValue={productCtx.productname}
                          id="url"
                          error = {productCtx && productCtx.error_message && productCtx.error_message.productname}
                          InputProps={{
                            readOnly: true,
                          }}
                          name="url"
                          label="URL"

                        />
                        <TextField
                          className={classes.helperinput}
                          variant="outlined"
                          margin="dense"
                          fullWidth
                          defaultValue={productCtx.product_categoy}
                          id="product_category"
                          InputProps={{
                            readOnly: true,
                          }}
                          error = {productCtx && productCtx.error_message && productCtx.error_message.product_categoy}
                          name="product_category"
                          label="Product Category"

                        />
                       <TextField
                          className={classes.helperinput}
                          variant="outlined"
                          margin="dense"
                          fullWidth
                          defaultValue={productCtx.product_type}
                          id="product_type"
                          error = {productCtx && productCtx.error_message && productCtx.error_message.product_type}
                          InputProps={{
                            readOnly: true,
                          }}
                          name="product_type"
                          label="Product Type"

                        />

            </Grid>
        </Grid>
    )
}



const useStyles2 = makeStyles(theme => ({
    progress: {
      margin: 'auto'
    },
  }));

export const ProductAttributes = withRouter(props => {

  // const classes = useStyles2();

  // const { data, loading, error } = useQuery(productCategory.query);

  // if(loading) return <div className="loaderdiv"><CircularProgress className={classes.progress} />
  // </div>
  // if(error) return <div>error</div>

  const _content = <ProductProvider value={{ data:[], mapper: [], mappertype:  "masterData" }} >
  <Component {...props}/>
  </ProductProvider>
  return _content
});
export default withRouter(ProductAttributes);
